// Validazione dati sensori
const data = msg.payload;

// Check campi obbligatori
const required_fields = ['timestamp', 'site_id', 'sensor_id', 'temperature', 'current_speed', 'visibility', 'luminosity', 'battery_level'];
const missing_fields = required_fields.filter(field => !(field in data));

if (missing_fields.length > 0) {
    node.warn(`Campi mancanti: ${missing_fields.join(', ')}`);
    return null;
}

// Validazione range valori
const validations = [
    { field: 'temperature', min: -10, max: 40, unit: 'Â°C' },
    { field: 'current_speed', min: 0, max: 5, unit: 'm/s' },
    { field: 'visibility', min: 0, max: 50, unit: 'm' },
    { field: 'luminosity', min: 0, max: 100000, unit: 'lux' },
    { field: 'battery_level', min: 0, max: 100, unit: '%' }
];

let is_valid = true;
for (const validation of validations) {
    const value = data[validation.field];
    if (value < validation.min || value > validation.max) {
        node.warn(`${validation.field}: ${value}${validation.unit} fuori range [${validation.min}-${validation.max}]`);
        is_valid = false;
    }
}

if (!is_valid) {
    return null;
}

// Aggiungi timestamp processamento
data.processed_at = new Date().toISOString();

// Log dati validi
node.log(`Dati validi ricevuti da ${data.site_id}/${data.sensor_id}`);

msg.payload = data;
return msg;

// Formatta dati per InfluxDB HTTP API
const data = msg.payload;

// Crea line protocol per InfluxDB
const timestamp = Math.floor(Date.now() * 1000000); // nanoseconds
const line = `dive_conditions,site_id=${data.site_id},sensor_id=${data.sensor_id},depth=${data.depth || 'unknown'} temperature=${data.temperature},current_speed=${data.current_speed},current_direction=${data.current_direction || 0}i,visibility=${data.visibility},luminosity=${data.luminosity},battery_level=${data.battery_level} ${timestamp}`;

msg.payload = line;
msg.headers = {
    'Authorization': 'Token dive-monitoring-token-2024',
    'Content-Type': 'text/plain; charset=utf-8'
};
msg.url = 'http://dive_influxdb:8086/api/v2/write?org=DivingCenter&bucket=dive_data&precision=ns';

return msg;

// Sistema di alert
const data = msg.payload;
const alerts = [];

// Alert temperatura critica
if (data.temperature < 12) {
    alerts.push({
        type: 'temperature',
        level: 'critical',
        message: `Temperatura critica: ${data.temperature}Â°C`,
        value: data.temperature,
        threshold: 12,
        site_id: data.site_id,
        timestamp: new Date().toISOString()
    });
}

// Alert corrente forte
if (data.current_speed > 1.5) {
    alerts.push({
        type: 'current',
        level: 'warning',
        message: `Corrente forte: ${data.current_speed}m/s`,
        value: data.current_speed,
        threshold: 1.5,
        site_id: data.site_id,
        timestamp: new Date().toISOString()
    });
}

// Alert visibilitÃ  scarsa
if (data.visibility < 5) {
    alerts.push({
        type: 'visibility',
        level: 'warning',
        message: `Scarsa visibilitÃ : ${data.visibility}m`,
        value: data.visibility,
        threshold: 5,
        site_id: data.site_id,
        timestamp: new Date().toISOString()
    });
}

// Alert batteria
if (data.battery_level < 20) {
    const level = data.battery_level < 10 ? 'critical' : 'warning';
    alerts.push({
        type: 'battery',
        level: level,
        message: `Batteria scarica: ${data.battery_level}%`,
        value: data.battery_level,
        threshold: 20,
        site_id: data.site_id,
        timestamp: new Date().toISOString()
    });
}

// Invia alert se presenti
if (alerts.length > 0) {
    node.log(`${alerts.length} alert generati per ${data.site_id}`);

    // Ritorna array di messaggi, uno per alert
    return alerts.map(alert => ({ 
        payload: alert,
        topic: `dive/${alert.site_id}/alerts`
    }));
}

return null;

// Lista siti con dati reali
const sites = [
    {
        site_id: "capo_vaticano",
        name: "Capo Vaticano",
        latitude: 38.6878,
        longitude: 15.8742,
        depth_category: "shallow",
        status: "online",
        last_update: new Date().toISOString()
    },
    {
        site_id: "tropea_reef",
        name: "Tropea Reef",
        latitude: 38.6767,
        longitude: 15.8989,
        depth_category: "deep",
        status: "warning",
        last_update: new Date().toISOString()
    },
    {
        site_id: "stromboli_east",
        name: "Stromboli East",
        latitude: 38.7891,
        longitude: 15.2134,
        depth_category: "surface",
        status: "online",
        last_update: new Date().toISOString()
    }
];

msg.payload = sites;
return msg;

// Prendi siteId dai parametri URL
const siteId = msg.req.params.siteId;

// Genera timestamp corrente
const now = new Date();
const timeVariation = Math.sin(now.getTime() / 30000);

// Configurazioni diverse per ogni sito
const siteConfigs = {
    "capo_vaticano": {
        baseTemp: 19.0,
        baseDepth: "shallow",
        baseCurrent: 0.8,
        baseVisibility: 22,
        baseLuminosity: 850,
        baseBattery: 78
    },
    "tropea_reef": {
        baseTemp: 16.5,
        baseDepth: "deep", 
        baseCurrent: 1.2,
        baseVisibility: 28,
        baseLuminosity: 65,
        baseBattery: 45
    },
    "stromboli_east": {
        baseTemp: 20.1,
        baseDepth: "surface",
        baseCurrent: 0.3,
        baseVisibility: 15,
        baseLuminosity: 1200,
        baseBattery: 92
    }
};

// Prendi configurazione per questo sito
const config = siteConfigs[siteId] || siteConfigs["capo_vaticano"];

// Genera variazioni casuali
const randomTemp = (Math.random() - 0.5) * 2;
const randomCurrent = (Math.random() - 0.5) * 0.4;
const randomVisibility = (Math.random() - 0.5) * 4;
const randomLuminosity = (Math.random() - 0.5) * 200;
const randomBattery = (Math.random() - 0.5) * 10;

// Crea dati che cambiano nel tempo
const currentData = {
    timestamp: now.toISOString(),
    site_id: siteId,
    sensor_id: `${siteId}_sensor_01`,
    depth: config.baseDepth,
    temperature: Number((config.baseTemp + timeVariation * 3 + randomTemp).toFixed(1)),
    current_speed: Number(Math.max(0, config.baseCurrent + timeVariation * 0.5 + randomCurrent).toFixed(1)),
    current_direction: Math.floor((45 + timeVariation * 60 + (Math.random() * 60)) % 360),
    visibility: Number(Math.max(1, config.baseVisibility + timeVariation * 5 + randomVisibility).toFixed(1)),
    luminosity: Number(Math.max(0, config.baseLuminosity + timeVariation * 200 + randomLuminosity).toFixed(1)),
    battery_level: Number(Math.max(0, Math.min(100, config.baseBattery + timeVariation * 15 + randomBattery)).toFixed(1))
};

node.log(`ðŸŒŠ Dati per ${siteId}: T=${currentData.temperature}Â°C, C=${currentData.current_speed}m/s`);

msg.payload = currentData;
return msg;

// Simula alert attivi - in futuro collegare a database alert
const alerts = [
    {
        type: "current",
        level: "warning",
        message: "Corrente forte rilevata",
        value: 1.8,
        threshold: 1.5,
        site_id: "capo_vaticano",
        timestamp: new Date().toISOString()
    },
    {
        type: "battery",
        level: "critical",
        message: "Batteria sensore scarica",
        value: 15,
        threshold: 20,
        site_id: "tropea_reef",
        timestamp: new Date().toISOString()
    }
];

// Filtra alert solo se condizioni realmente critiche
const activeAlerts = alerts.filter(alert => {
    // Aggiungi logica per determinare alert realmente attivi
    return Math.random() > 0.5; // 50% chance - sostituire con logica reale
});

msg.payload = activeAlerts;
return msg;
